import React from 'react';
import { Button } from '@react95/core';
import styled from 'styled-components';

const StyledLink = styled.a`
  margin-bottom: 10px;
`;

function Resume({ content }) {
  const { workExperience, education, resumeLink } = content;

  return (
    <div>
      <h2>Work Experience</h2>
      {workExperience.map((exp, idx) => (
        <div key={idx}>
          <p><b>{exp.jobTitle}</b></p>
          <p>
           {exp.company} | {exp.location}
          </p>
          <p>{exp.period}</p>
          <ul>
            {exp.accomplishments.map((a, idx) => (
              <li key={idx}>{a}</li>
            ))}
          </ul>
          <br />
        </div>
      ))}
      <hr />
      <h2>Education</h2>
      {education.map((ed, idx) => (
        <div key={idx}>
          <p>
            <b>{ed.credit}</b>
          </p>
          <p>{ed.place}</p>
          <p>
            <b>{ed.gpa}</b>
          </p>
          <p>{ed.period}</p>
          <br />
        </div>
      ))}
      <StyledLink href={resumeLink} download>
        <Button style={{ fontSize: '14px' }} className="pointer">
          Download Resume
        </Button>
      </StyledLink>
    </div>
  );
}

export default Resume;
