import React from 'react';
import { GlobalStyle, ThemeProvider } from '@react95/core';
import { createGlobalStyle } from 'styled-components';
import DataService from './services/dataService';
import DataContext from './contexts/dataContext';
import Taskbar from './Taskbar';
import Desktop from './Desktop';
import styled from 'styled-components'; // Add this import

// Add your background image import
import backgroundImage from './assets/background.png'; // Update path to your actual image

const dataService = new DataService();

const BodyFontSizeOverride = createGlobalStyle`
  body {
    font-size: 15px
  }
`;

// Create a styled component for the background
const BackgroundContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: -1;
  background-image: url(${props => props.image});
  background-size: cover;
  background-position: center;
`;

const App = () => (
  <DataContext.Provider value={dataService}>
    <ThemeProvider>
      <GlobalStyle />
      <BodyFontSizeOverride />
      <BackgroundContainer image={backgroundImage} /> {/* Add this line */}
      <Desktop />
      <Taskbar />
    </ThemeProvider>
  </DataContext.Provider>
);

export default App;
